  const [table, setTable] = useState("")

  useEffect(() => {
    fetch(URL)
    .then(r => r.json())
    .then((data) => {
        setTable(data)
    })
  })

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>Date</th>
          <th>Category</th>
          <th>Description</th>
          <th>Amount</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>{date}</td>
          <td>{category}</td>
          <td>{description}</td>
          <td>{amount}</td>
        </tr>
      </tbody>
    </Table>
  );
}

export default transaction;
